export function generateSeoPages(keywords: string[]): InsertEmojiPage[] {
  return keywords.map(keyword => ({
    slug: slugify(keyword),
    title: `${keyword.split(' ').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ')} - Copy & Paste`,
    keyword,
    metaDescription: `Copy and paste ${keyword} instantly! Find the best ${keyword} to use in messages, social media, and more. One click copy.`,
    relatedEmojis: [],
    isGenerated: false,
  }));
}
